SET FOREIGN_KEY_CHECKS=0;

-- ----------------------------
-- Table structure for cms_article
-- ----------------------------
DROP TABLE IF EXISTS `cms_article`;
CREATE TABLE `cms_article` (
  `article_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增id',
  `article_title` varchar(32) NOT NULL COMMENT '文章名称',
  `article_content` text NOT NULL COMMENT '文章内容',
  `article_read_num` int(11) DEFAULT '100',
  `article_create_time` int(11) NOT NULL COMMENT '创建时间',
  `article_update_time` int(11) DEFAULT NULL COMMENT '修改时间',
  `article_status` enum('0','1','-1','2') DEFAULT '0' COMMENT '0未发布；1已发布；-1已删除；2审核未通过',
  `article_category_id` int(11) NOT NULL COMMENT '分类id',
  `article_user_id` int(10) NOT NULL COMMENT '文章的作者',
  PRIMARY KEY (`article_id`),
  KEY `article_user_id` (`article_user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=30 DEFAULT CHARSET=utf8 COMMENT='文章表';

-- ----------------------------
-- Records of cms_article
-- ----------------------------
INSERT INTO `cms_article` VALUES ('1', '(分享&交流) Sublime Text 翼帆远航定制版', '<p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">对于开发者而言，拥有一款强大并且顺手的开发工具无疑能大大的提升工作的效率，而 Sublime Text 正是这样一款人见人爱的开发神器，精妙的界面有助于让你更加专注于代码编写的艺术，丰富的插件有助于让你的开发工作事半功倍。由于 Sublime Text 本身具备高度的独立性和便携性，因此你可以随时将你的专属 Sublime Text 打包起来留作备用，或者分享给好友。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">本文专用于分享我定制的 Sublime Text。我希望通过这篇文章，与大家交流如何更好的使用&nbsp;Sublime Text，如果你有更好用的插件或者更高效的使用技巧，也欢迎留言分享出来。在本文中，除了分享「Sublime Text 翼帆远航定制版」的下载链接以外，我还会列出其中所安装的插件，方便翼友们定制自己的 Sublime Text。</p><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">软件截图</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/04/hychen_SublimeText_01.png\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone wp-image-8692\" src=\"http://www.ipeld.net/wp-content/uploads/2015/04/hychen_SublimeText_01.png\" alt=\"hychen_SublimeText_01\" width=\"800\" height=\"521\" original=\"http://www.ipeld.net/wp-content/uploads/2015/04/hychen_SublimeText_01.png\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto;\"/></a></p><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">插件列表</h3><ul style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>Package Control 安装其它插件的基础</p></li><li><p>All Autocomplete 它会搜索所有已打开的文件来匹配代码提示词</p></li><li><p>AutoFileName 自动填充文件名</p></li><li><p>BracketHighlighter 它能为 Sublime Text 提供括号、引号这类的高亮功能</p></li><li><p>Color Highlighter 当 CSS 代码中出现类似 #FFFFFF 表示颜色的单词时，该插件会自动为这个单词上色</p></li><li><p>ConvertToUTF8 将文件编码转换成通用的&nbsp;UTF-8</p></li><li><p>DocBlockr 自动识别函数，生成注释（使用方法：在函数定义的上一行输入 /** 再按 Tab 键）</p></li><li><p>Emmet 它允许你使用类似 CSS 选择器的形式快速生成 HTML 代码</p></li><li><p>FileDiffs 它允许你查看两个不同文件的差异，对比的对象还可以是剪贴板文本或当前文件最近一次保存的状态</p></li><li><p>SidebarEnhancements 增强了左侧边栏文件的右键菜单</p></li><li><p>SublimeCodeIntel 代码提示插件，支持各种语言</p></li><li><p>Zen Tabs 智能优化标签栏的显示，避免出现 20 个标签并存导致每个标签的宽度过小的情况</p></li></ul><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">使用说明</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">解压下载到的 Sublime Text 压缩包，运行“Sublime Text 3sublime_text.exe”即可使用，也可以把它移动到 U 盘里。Sublime Text 是完全独立的，所有配置文件都储存于同一个目录中，可与系统中其它的 Sublime Text 并存，即使同时运行也是完全没有问题的。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">Ctrl + Shift + P 调出命令面板，输入 instp 筛选出 Install Package，进入后可搜索插件名安装插件；在命令面板中输入 listp 进入 List Packages 可查看所有已安装的插件；输入 remp 进入 Remove Package 可删除已安装的插件。</p><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">下载地址</h3><ul style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>链接：<a href=\"http://pan.baidu.com/s/1jGtdu2y\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">http://pan.baidu.com/s/1jGtdu2y</a></p></li><li><p>密码：lmxz</p></li></ul><p><br/></p>', '99', '1440669314', '1441013926', '1', '5', '1');
INSERT INTO `cms_article` VALUES ('9', '习近平签令奖励5个单位和29名个人', '<p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; font-family: &#39;Microsoft YaHei&#39;, u5FAEu8F6Fu96C5u9ED1, Arial, SimSun, u5B8Bu4F53; white-space: normal;\">中央军委主席习近平日前签令奖励5个单位和29名个人：给2个单位、1名个人授予荣誉称号；给3个单位、28名个人记功。</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; font-family: &#39;Microsoft YaHei&#39;, u5FAEu8F6Fu96C5u9ED1, Arial, SimSun, u5B8Bu4F53; white-space: normal;\"><strong>　　中央军委主席习近平签署通令给3个单位、28名个人记功</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; font-family: &#39;Microsoft YaHei&#39;, u5FAEu8F6Fu96C5u9ED1, Arial, SimSun, u5B8Bu4F53; white-space: normal;\">　　新华社北京8月26日电 中央军委主席习近平日前签署通令，给3个单位、28名个人记功。</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; font-family: &#39;Microsoft YaHei&#39;, u5FAEu8F6Fu96C5u9ED1, Arial, SimSun, u5B8Bu4F53; white-space: normal;\">　　给在完成任务中做出突出贡献的92730部队90分队记一等功，给在科研工作中做出突出贡献的国防科学技术大学计算机学院计算机研究所记一等功，给在单位建设中取得突出成绩的96167部队记二等功。</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; font-family: &#39;Microsoft YaHei&#39;, u5FAEu8F6Fu96C5u9ED1, Arial, SimSun, u5B8Bu4F53; white-space: normal;\">　　给61786部队研究员罗晓广，61886部队研究员王过中，总参谋部某研究所高级工程师周彩根，信息工程大学信息技术研究所教授季新生，第三○二医院副院长兼医务部主任、主任医师李进，军事医学科学院附属医院造血干细胞移植科主任、主任医师陈虎，63729部队高级工程师淳静记一等功；给海军航空工程学院兵器科学与技术系兵器教研室原教授马登武，国防大学战役兵棋系统教研室原副主任、副教授张国春追记一等功。</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; font-family: &#39;Microsoft YaHei&#39;, u5FAEu8F6Fu96C5u9ED1, Arial, SimSun, u5B8Bu4F53; white-space: normal;\">　　给南京陆军指挥学院军事训练系教授胡坚，第三军医大学药学系微生物与生化药学教研室主任、教授邹全明，第四军医大学第一附属医院骨科主任、主任医师、教授罗卓荆，军事医学科学院放射与辐射医学研究所药理毒理研究室主任、研究员高月，海军工程大学动力工程学院某研究所教授何琳，海军航空工程学院信息融合研究所教授何友记二等功。</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; font-family: &#39;Microsoft YaHei&#39;, u5FAEu8F6Fu96C5u9ED1, Arial, SimSun, u5B8Bu4F53; white-space: normal;\">　　给61081部队研究员韩春好，信息工程大学网络空间安全学院教授赵荣彩，信息工程大学网络空间安全学院教授祝跃飞，解放军总医院内科临床部主任医师、教授杨明会，后勤工程学院军事土木工程系教授程华，第三军医大学第一附属医院检验科主任、主任医师、教授府伟灵，军事医学科学院基础医学研究所研究员钱令嘉，海军装备研究院某研究所研究员邱志明，海军工程技术管理办公室副主任、高级工程师李决龙，空军机要研究所科技处高级工程师王建华，第二炮兵指挥学院工程保障系阵地保障教研室教授张金城，第二炮兵装备研究院总工程师、研究员肖龙旭，国防大学信息作战与指挥训练教研部教授胡晓峰记三等功。（完）</p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; font-family: &#39;Microsoft YaHei&#39;, u5FAEu8F6Fu96C5u9ED1, Arial, SimSun, u5B8Bu4F53; white-space: normal;\">　　<strong>中央军委主席习近平签署命令给2个单位、1名个人授予荣誉称号</strong></p><p style=\"margin-top: 15px; margin-bottom: 15px; padding: 0px; line-height: 2em; font-family: &#39;Microsoft YaHei&#39;, u5FAEu8F6Fu96C5u9ED1, Arial, SimSun, u5B8Bu4F53; white-space: normal;\">　　新华社北京8月26日电 中央军委主席习近平日前签署命令，给2个单位、1名个人授予荣誉称号。</p><p><br/></p>', '1000', '1440665977', '1440981484', '1', '4', '2');
INSERT INTO `cms_article` VALUES ('19', '云中的一张白纸：Notepad.cc', '<p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">每次打开 notepad.cc，你都将得到一张独一无二的云端白纸，与这张白纸相对应的是一个独一无二的网址，任何访问这个网址的人都可以编辑这张白纸上的内容。如果你希望只有特定的人才可以编辑，你可以为这张白纸设置密码；如果你希望这张白纸对于别人是只读的，你可以通过 share this 按钮生成只读链接。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/06/paper.jpg\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone wp-image-8750\" src=\"/ueditor/php/upload/image/20150831/1441013166116482.jpg\" alt=\"paper\" width=\"256\" height=\"256\" original=\"http://www.ipeld.net/wp-content/uploads/2015/06/paper.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto;\"/></a></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">有了 notepad.cc，不管好友在用电脑还是手机、有没有上 QQ、电脑上有没有 Word，你都可以将信息以最快的速度分享给好友。把 notepad.cc 加入书签栏，不管是来电中的重要信息，还是一闪而过的灵感，都不会错过，点开即记录，快速而专注，免注册还无广告，整个界面非常清爽！竟没有一个多余的按钮！</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/06/notepad-cc.png\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone wp-image-8745\" src=\"/ueditor/php/upload/image/20150831/1441013166429758.gif\" alt=\"notepad-cc\" width=\"640\" height=\"353\" original=\"http://www.ipeld.net/wp-content/uploads/2015/06/notepad-cc.png\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto;\"/></a></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">你不必担心电脑的突然断电，信息在你记录的同时也会被实时保存到云端，电脑恢复后，通过浏览器历史可以让你快速回到最佳编辑状态。</p><p><br/></p>', '100', '1440669333', '1441013167', '1', '6', '2');
INSERT INTO `cms_article` VALUES ('20', '如何评价微云和360云盘逐渐淡出同步功能的做法？', '<p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">用一句话来概括：在成本和精力都有限的情况下，想要同时开发出在同领域下有竞争力的网盘和同步盘产品是很困难的，在权衡付出与回报以后，他们选择了专注做网盘。</strong></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">知乎上已有其它问题谈及到：TB级别的云储存容量给大企业带来的硬件成本上的开销是可接受的。&nbsp;<a class=\"\" href=\"http://www.zhihu.com/question/21591490\" data-editable=\"true\" data-title=\"为什么国内的网盘公司都在 TB 的级别上竞争，成本会不会太高？ - 网盘\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">为什么国内的网盘公司都在 TB 的级别上竞争，成本会不会太高？ - 网盘</a>&nbsp;但是，一家企业能够长久的做云储存产品需要考量的因素不仅仅在硬件成本上，除了人力物力的成本，还要考虑产品的竞争力，做竞品分析，还要预测用户需求的未来走势。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">咱们来谈一谈“同步盘”</strong></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">“同步盘”满足的是 —— 用户希望能够在不同设备间实时访问文件的最新版本的需求。从这一点来看，市面上所有的同步盘产品都做到了这一点，但关键的问题是如何将同步盘做得像 Dropbox 那样好用？那样有竞争力？</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">“稳定、极速、安全”是一款同步盘产品应具有的基本素质，而一款合格的同步盘产品应该做到：</p><ol style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>支持局域网同步加速 (节省用户的时间)</p></li><li><p>支持 HTTPS 加密传输 (保护用户的脑力劳动成果)</p></li><li><p>支持文件增量传输 (帮用户省流量)</p></li><li><p>支持文件在线预览</p></li></ol><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">另外，一款优秀的同步盘产品还应该做到：</p><ol style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>支持文件历史版本功能</p></li><li><p>支持密码保护的功能</p></li></ol><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">可惜的是，市面上大部分同步盘产品所宣传的亮点都体现在容量上，而不是这款产品能为用户节省多少时间、节省多少流量开销、满足用户多少与文件相关的需求如协作和分享。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">确切的说，打“容量战” —— 更大的容量并不能成为自身产品的核心竞争力。如上文所说，随着用户量的激增，同一文件被不同用户上传的概率大大增加，而同一文件在服务器上只会储存一份，在这样的前提下，硬件成本的增加不值得一提。这样一来，所有云储存产品都推出海量的存储空间，那么，你的竞争力在哪？</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">所以说，放下浮躁，静下心来用心打造一款产品，站在用户的角度来设计功能，是构建自身产品核心竞争力的根本。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">查看我在知乎上的回答：<a href=\"http://www.zhihu.com/question/29159457/answer/46245246\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">如何评价微云和360云盘逐渐淡出同步功能的做法？</a></p><p><br/></p>', '100', '1440676058', '1441013960', '1', '3', '1');
INSERT INTO `cms_article` VALUES ('21', '设计师专属的移动电源 - VOJO+ Onemini 体验', '<p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">长一段时间以来，当大家谈论起移动电源，总会不自觉地说起它的容量，就连京东天猫以“移动电源”为关键字搜索，都可以发现大部分厂商都以“低价大容量”为卖点，却很少有站在用户需求和使用场景角度设计的移动电源。但今天体验的 VOJO+ Onemini 就是这么一款从用户需求与使用场景出发而设计的移动电源，美观精致、坚固实用，能够满足设计师对高品位的追求。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_01.png\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone size-medium wp-image-8565\" src=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_01-450x269.png\" alt=\"vojo+onemini_01\" width=\"450\" height=\"269\" original=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_01-450x269.png\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto;\"/></a></p><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">物尽其用</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">与众不同的是，VOJO+ Onemini 自带可伸缩的插头，这样一方面它可以作为移动电源，另一方面还可以作为电源适配器。通常在家，你可以将低电量的手机插在低电量的 VOJO+ Onemini 上，然后再把它插在插座上，这样一来它会优先为手机充电，然后才为自己充电。如此人性化的设计有力的抓住了“用户时常先为手机充电，而后又忘记充移动电源”的痛点。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_03.png\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone size-full wp-image-8568\" src=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_03.png\" alt=\"vojo+onemini_03\" width=\"353\" height=\"380\" original=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_03.png\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto;\"/></a></p><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">轻便够用</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">VOJO+ Onemini 很轻，只有 97g，它还很小巧，长 72mm 宽 30mm 高 35mm，无论拿在手上还是放在包里，都轻盈无比。另外，手机本身的电池容量加上它&nbsp;2800mAh 的电源电量可以让你更放心的使用手机。之前我随身携带的移动电源是爱国者的 OL11200，虽然有 11200 mAh 的电源电量，但平均一天下来，也用不了多少电量，反倒还得多负重 240g。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">VOJO+ Onemini 本身定位于每天随身携带的移动电源，小巧轻便，你可以带着它去上班、上学或者短程出游。而高容量的移动电源意味着更重的质量和更大的体积，显然更适合出远门或者旅游。VOJO+ Onemini 在电源电量和便携性方面的权衡做的很好。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_02.png\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone size-medium wp-image-8567\" src=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_02-450x299.png\" alt=\"vojo+onemini_02\" width=\"450\" height=\"299\" original=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_02-450x299.png\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto; display: inline;\"/></a></p><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">时尚精致</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">VOJO+ Onemini 提供多种颜色的版本，还支持图案定制。我买的是蜂黄色的，颜色很纯正，与变形金刚电影中大黄蜂的配色相同，这点我个人非常喜欢。另外，它带有指示灯的一面采用了特殊的漆面，手感极佳，擦拭方便，其它侧面磨砂的手感也很棒！唯一遗憾的是，它的输出电流是 1A，这意味着它不是 iPad 的最佳伴侣。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_04.png\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone size-medium wp-image-8571\" src=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_04-450x300.png\" alt=\"vojo+onemini_04\" width=\"450\" height=\"300\" original=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_04-450x300.png\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto; display: inline;\"/></a></p><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">意外惊喜</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">让我感到意外的是，VOJO+ Onemini 还随袋附赠了一条数据线，是橙色的，其实我更希望是蜂黄色的。另外，让我感到惊喜的是，这条数据线的两端是有磁性吸在一起的，携带和使用都很方便。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_05.jpg\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone size-medium wp-image-8573\" src=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_05-450x338.jpg\" alt=\"vojo+onemini_05\" width=\"450\" height=\"338\" original=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_05-450x338.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto; display: inline;\"/></a></p><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_06.jpg\" class=\"highslide-image\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(68, 68, 68); text-decoration: none; transition: all 0.2s ease-in-out; -webkit-transition: all 0.2s ease-in-out; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone size-medium wp-image-8574\" src=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_06-338x450.jpg\" alt=\"vojo+onemini_06\" width=\"338\" height=\"450\" original=\"http://www.ipeld.net/wp-content/uploads/2015/01/vojo-onemini_06-338x450.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto; display: inline;\"/></a></h3><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">结语</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">总的来说这次购物体验非常愉快，虽然 69 元的售价加上 2800mAh 的电源电量在部分人眼里是不值得，但在我的眼里，表里如一的品质是我更看重的，VOJO+ Onemini 时尚的外观、坚固的外表、轻便的体量和实用的设计都是其它“高性价比”移动电源所无法企及的。</p><p><br/></p>', '100', '1440676148', '1441014178', '1', '4', '2');
INSERT INTO `cms_article` VALUES ('22', '浅谈 2015 羊年春节的红包大战', '<p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">如果说 2014 马年春节的重头戏是打车软件大战，那么今年则是红包大战。虽然每年春节科技大佬们的战争在形式和内容上都有所不同，但其最终的目的都是相同的 —— 提高市场份额和培养用户习惯，为此，砸再多钱都值得。透过支付宝和微信这两位各有千秋的对手所发起的红包大战，我们看到移动支付正崛地而起，并逐渐被大众所接受，将成为未来普遍的支付方式。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/02/hongbao.jpg\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone size-full wp-image-8587\" src=\"http://www.ipeld.net/wp-content/uploads/2015/02/hongbao.jpg\" alt=\"hongbao\" width=\"412\" height=\"247\" original=\"http://www.ipeld.net/wp-content/uploads/2015/02/hongbao.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto;\"/></a></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">在今年的红包大战中，我体会最深的是支付宝红包和微信红包。</p><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">支付宝红包</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">支付宝打地鼠赢红包的活动具备很高的可玩性，它并不要求用户在整点抢红包，而是在整点过后的十五分钟内，用户都可以通过玩游戏的方式赢取红包，游戏音效可爱、调皮，很是动人心弦，特别是一群人一起打地鼠的时候，现场气氛 High 到爆，最后连爸妈也参与其中！支付宝抓住了用户的心理（爱贪小便宜、爱热闹），并借此加强了用户的参与感，提高了用户对支付宝这个品牌的认知。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">支付宝红包的另一个亮点是接龙红包，用户将任意金额塞入红包，让好友猜，猜中拿钱，猜不中则继续分享，当发展的下线猜中时，上线也可以分到钱，我想这是接龙红包最成功的地方，大大增加了用户分享的积极性。如果用户分不到钱，那么就没有足够的积极性去发展下线了。这也是支付宝红包的数量能病毒式增长下去的原因。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/02/jielong_hongbao.png\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone size-medium wp-image-8597\" src=\"http://www.ipeld.net/wp-content/themes/sahifa/images/grey.gif\" alt=\"jielong_hongbao\" width=\"254\" height=\"450\" original=\"http://www.ipeld.net/wp-content/uploads/2015/02/jielong_hongbao-254x450.png\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto;\"/></a></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">不过，通过这次支付宝的接龙红包，我发现了一个问题，也是支付宝的弱势所在 —— 在社交平台上的弱势。微信和QQ近乎是互联网社交的代名词，无论何种内容的分享，用户总是愿意将其分享至微信或QQ，而作为支付宝对手的微信和QQ，屏蔽了来自支付宝的链接，使得支付宝不得不使用口令的方式来传递红包，显得十分被动。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">我有个建议，在联系人对话界面中，强化发送文字等联络感情和交换信息的入口，而不仅仅凸显金钱的交换，另外还可以整合淘宝和天猫，允许用户分享已购买的商品与评价，推动电商业务的发展，虽然旺旺也可以做到，但有多少人是在强关系的基础上，用旺旺来进行买家与买家之间的交流呢？适当的整合功能，增加用户进入该对话界面的次数，避免让用户误以为只有转账才需要进入这个界面，另外，也要有意识的培养用户进入该界面的习惯。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/02/image.jpg\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone size-medium wp-image-8591\" src=\"http://www.ipeld.net/wp-content/uploads/2015/02/image-254x450.jpg\" alt=\"image\" width=\"254\" height=\"450\" original=\"http://www.ipeld.net/wp-content/uploads/2015/02/image-254x450.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto; display: inline;\"/></a></p><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">微信红包</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">微信红包的亮点在于与春晚的互动，微信需要更大的广告平台，春晚需要更高的收视率，二者一拍即合。全家人围坐在电视机前摇手机的壮观场面，增强了用户的参与感，也提高了用户对微信支付这个品牌的认知和信任感。摇到的现金放入零钱，可以提现，这是父母这辈人对微信支付产生信任感重要的一环。摇到的优惠券放入卡包，抓住了用户希望得到实惠，但又苦于太多的卡片导致皮包又厚又重，实在不方便携带的痛点。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/02/weixin_hongbao.png\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone size-medium wp-image-8598\" src=\"http://www.ipeld.net/wp-content/themes/sahifa/images/grey.gif\" alt=\"weixin_hongbao\" width=\"254\" height=\"450\" original=\"http://www.ipeld.net/wp-content/uploads/2015/02/weixin_hongbao-254x450.png\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto;\"/></a></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">当你看到上图，是不是忍不住去点“拆红包”？</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">微信的用户基数很大，在家庭群、工作群和同学群发一个红包，大家奔走相告，惊呼红包来了，然后红包被哄抢而光，这种状况是前所未有的，有抢多了的窃喜，也有抢少了的叹气，更有缺德人把 0.1 元塞进 10 个红包。总而言之，大家玩得不亦乐乎。场面远大于去年的打车软件大战。</p><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">红包的影响力</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">可别小瞧了这些“小小”的红包，它们背后的“移动支付”才是真正的主角，让我们一起总结，在这场红包大战过后，移动支付会对我们的生活产生何种影响：</p><ol style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>越来越多的线下消费场所支持移动支付，有手机取代皮包之势，如沃尔玛购物、必胜客用餐等。</p></li><li><p>亲友海外代购、聚餐AA付款等场景都将使用电子钱包来完成转账操作，有电子钱包取代现金和银行卡之势。</p></li><li><p>用户将更加注重电子账户的安全，服务提供商将推出更多措施以保障用户财产的安全。</p></li></ol><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">对于科技大佬们，这场红包大战所产生的收益才刚刚开始：</p><ol style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>大量用户绑定银行卡，并体验过支付和提现的操作，降低了用户于线下场景使用移动支付的门槛，科技大佬们借此赚取丰厚的分成。</p></li><li><p>借此红包大战，科技大佬们提高了产品的市场占有率，为今后推广更多产品创造了机会。</p></li><li><p>借此红包大战，用户加深了对科技大佬手中产品的认知和信任，使用户更热衷于使用这些产品。</p></li></ol><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">现金、银行卡和各类卡券都被电子钱包代替了，这是未来的趋势。</p><p><br/></p>', '100', '1440678520', '1441013999', '2', '3', '2');
INSERT INTO `cms_article` VALUES ('23', '文章6WordPress 头像溢出边界及解决方案', '<p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">众所周知，Gravatar 在国内被墙无法访问，而采用 Gravatar 作为头像系统的 WordPress 自然也受到了波及。因此目前来讲，如果想在国内正常显示头像，除了使用第三方评论插件以外，还可以使用翼帆远航之前分享的<a href=\"http://www.ipeld.net/archives/8395.html\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">头像URL替换法</a>，目前翼帆远航使用的就是这个方法。鉴于&nbsp;Gravatar 官方的 SSL 头像调用URL依然可用，因此可以通过 WordPress 过滤器在最终生成头像URL之前，将被墙的 http://2.gravatar.com 替换为可访问的 https://secure.gravatar.com，这样一来头像就可以正常显示了。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">但新版的&nbsp;WordPress 为了更好的支持响应式图片，在所有的头像 &lt;img&gt; 标签中增加了 srcset 属性，用于获取 2 倍尺寸大小的头像，以便在 iPhone、Macbook Pro 等视网膜屏上显示更高清的头像，这样一来，头像 &lt;img&gt; 标签中就包含了&nbsp;2 段分别用于获取 1x 和 2x 头像的URL，使得原先头像URL替换法所使用的正则表达式失效，造成匹配错误。最终体现在视觉上就是：博客内所有头像都溢出了边界，而且尺寸大小是正常尺寸的 2 倍，很影响博客界面的美观性。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">为了解决该问题，翼帆远航重写了正则表达式和替换规则，现经测试，头像可以正常显示了。具体的操作步骤是：参阅之前的文章《<a href=\"http://www.ipeld.net/archives/8395.html\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">Gravatar 头像被墙及解决方法</a>》，用新的代码替换旧的代码。</p><p><br/></p>', '100', '1440678607', '1441013195', '1', '5', '2');
INSERT INTO `cms_article` VALUES ('25', '文章7', '<p>asd</p>', '100', '1440679520', '1440997814', '-1', '0', '0');
INSERT INTO `cms_article` VALUES ('26', '(实用技巧) 让 Win10 的 文件资源管理器 默认不显示“快', '<p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">微软在最新的 Windows 10 中引入了“快速访问”的概念。当你点开任务栏的“文件资源管理器”时，默认会打开“快速访问”页面，而不是之前&nbsp;Windows 版本中的“这台电脑”。你可以在“快速访问”中直接地找到最近使用的文件及文件夹，而不必层层深入地去寻找。这样的设计确实能提高我们日常使用电脑的效率，但也会泄露我们的隐私。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/08/win10-quick-access-01.png\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone wp-image-8843\" src=\"http://www.ipeld.net/wp-content/uploads/2015/08/win10-quick-access-01.png\" alt=\"win10-quick-access-01\" width=\"640\" height=\"400\" original=\"http://www.ipeld.net/wp-content/uploads/2015/08/win10-quick-access-01.png\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto;\"/></a></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">因此，翼帆远航总结了一套方法，允许你设置：</p><ul style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>打开“文件资源管理器”时默认显示的是“快速访问”还是“这台电脑”</p></li><li><p>“快速访问”页面中要显示的项目。</p></li></ul><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">设置打开“文件资源管理器”的默认行为</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1. 打开“文件资源管理器”。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2. 在菜单栏中依次选择“查看→选项”，以打开“文件夹选项”窗口。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/08/win10-quick-access-02.png\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone wp-image-8845\" src=\"http://www.ipeld.net/wp-content/uploads/2015/08/win10-quick-access-02.png\" alt=\"win10-quick-access-02\" width=\"640\" height=\"136\" original=\"http://www.ipeld.net/wp-content/uploads/2015/08/win10-quick-access-02.png\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto; display: inline;\"/></a></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3. 在“常规”选项卡中的第一项选择“快速访问”或“这台电脑”。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/08/win10-quick-access-03.png\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone wp-image-8846\" src=\"http://www.ipeld.net/wp-content/uploads/2015/08/win10-quick-access-03.png\" alt=\"win10-quick-access-03\" width=\"360\" height=\"165\" original=\"http://www.ipeld.net/wp-content/uploads/2015/08/win10-quick-access-03.png\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto; display: inline;\"/></a></p><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">设置“快速访问”页面中要显示的项目</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">同样在“文件夹选项”窗口的“常规”选项卡，于“隐私”一栏设置在“快速访问”页面中要显示的项目。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/08/win10-quick-access-04.png\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone wp-image-8847\" src=\"http://www.ipeld.net/wp-content/uploads/2015/08/win10-quick-access-04.png\" alt=\"win10-quick-access-04\" width=\"360\" height=\"215\" original=\"http://www.ipeld.net/wp-content/uploads/2015/08/win10-quick-access-04.png\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto; display: inline;\"/></a></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">PS：点击“清除”按钮可以清除已有历史记录。</p><p><br/></p>', '2000', '1441013079', '1441014186', '1', '0', '0');
INSERT INTO `cms_article` VALUES ('27', '翼帆远航建站 3 周年，回顾过去、展望未来', '<p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">时间过得真快，转眼 3 年过去了。这个博客从我大一的时候就开始陪伴我，直至今日，见证了我的成长与进步。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">我越来越觉得，建这个博客是对的，因为是它让我逐渐养成了独立思考的习惯，形成了自己独立的价值观。另外，是它让我懂得了什么叫做持之以恒的做一件有意义的事：</p><ol style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>从时间管理的角度来讲，独立思考、整理思路和输出文章是件重要但不紧急的事情。虽然它无法在短时间内改善你的生活，但它会潜移默化的使你的条理变得更加清晰，使你更善于解决问题，从而变得更加优秀。</p></li><li><p>从知识管理的角度来讲，输出文章属于知识管理的重要一环 —— 分享知识。知识在经过“获取→学习→保存→使用”之后，我们通过将知识主动的分享给他人，使自己对知识的理解更加深刻和全面。分享知识，不仅可以帮助他人，还可以通过博客上的评论互动，发现自己对某个知识点的遗漏或理解的不正确。</p></li></ol><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">回顾过去</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/07/review-the-past.jpg\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone wp-image-8801\" src=\"http://www.ipeld.net/wp-content/uploads/2015/07/review-the-past.jpg\" alt=\"review-the-past\" width=\"640\" height=\"360\" original=\"http://www.ipeld.net/wp-content/uploads/2015/07/review-the-past.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto;\"/></a></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">在过去的 1 年里，我觉得我的进步很大，具体体现在以下几个方面：</p><ul style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; text-decoration: underline;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">做事更有条理</strong></span>：在事情开始前，先用 Doit.im 做好计划与提醒，然后心中预先演练，做足充分的准备。</p></li><li><p><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; text-decoration: underline;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">做事更加高效</strong></span>：简化关注（只关心真正重要的）、追求少而精（常做减法，慎做加法）。每次前进一小步（单位时间内专注做一件有意义的事），一步一个脚印，基础扎实，事情才能高效的完成，从而增加成就感，良性循环。</p></li></ul><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">在过去的 1 年里，这些工具对我的帮助很大：</p><ol style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p>Doit.im —— 时间管理</p></li><li><p>印象笔记 —— 知识管理</p></li><li><p>XMind —— 思维导图，整理思路</p></li><li><p>Listary —— 快速找到文件</p></li><li><p>酷盘 —— 一处更新，随处可取的文件云管家</p></li></ol><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">展望未来</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/07/look-forward-to-the-future.jpg\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone wp-image-8802\" src=\"http://www.ipeld.net/wp-content/uploads/2015/07/look-forward-to-the-future.jpg\" alt=\"look-forward-to-the-future\" width=\"640\" height=\"360\" original=\"http://www.ipeld.net/wp-content/uploads/2015/07/look-forward-to-the-future.jpg\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto; display: inline;\"/></a></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">在建站 3 年后的今天，我已从学校走向了工作岗位。在接下来的一年里，我希望在以下方面有所坚持和进步：</p><ul style=\"list-style-type: none;\" class=\" list-paddingleft-2\"><li><p><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; text-decoration: underline;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">坚持健身</strong></span>：想要保持健身的习惯，首先要制定符合自身情况和短期目标的健身计划。</p></li><ul style=\"list-style-type: circle;\" class=\" list-paddingleft-2\"><li><p>由于参加工作，角色发生了改变，原先针对校园生活的健身计划在现在来看，有很多不可行的地方。</p></li><li><p>光有长期目标是不够的，还需细化成短期目标，有针对性的进行训练，才有成效。</p></li></ul><li><p><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; text-decoration: underline;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">有计划的学习专业技能和提升自己的软技能</strong></span></p></li><ul style=\"list-style-type: circle;\" class=\" list-paddingleft-2\"><li><p>根据自己的学习能力与时间安排，制定计划以便深入的学习专业技能，并用印象笔记来管理学习进度和笔记。</p></li><li><p>有意识的总结自己在做某件事情的时候，还可以做的更好的地方，以便下次提高效率。</p></li></ul><li><p><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; text-decoration: underline;\"><strong style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\">增加与父母、好友的交流机会</strong></span></p></li><ul style=\"list-style-type: circle;\" class=\" list-paddingleft-2\"><li><p>参加工作以后，时间变得更加宝贵，但也应有意识的与父母进行沟通和交流，在感谢养育之恩的同时，也能化解生活中的烦恼。</p></li><li><p>眼睛是心灵的窗户，我相信通过面对面的交流，不仅能增进互相的了解，还能收获真挚的友谊。</p></li></ul></ul><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">赠言</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">我很赞同一句话：让优秀成为一种习惯，让努力成为一种常态。这是我在高中（厦门科技中学）最常听到的一句话，这句话默默地陪伴了我三年，不断地激励我成长和进步。</p><p><br/></p>', '1500', '1441013127', '1441014378', '1', '6', '0');
INSERT INTO `cms_article` VALUES ('28', '实用的 Chrome 扩展程序整理与推荐', '<p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">Chrome&nbsp;是一款非常优秀的网页浏览器，它矢志不渝的为用户提供安全、极速、稳定和沉浸式的网页浏览体验，其简洁清爽的界面为用户创造了更为高效的工作环境。另外，数以万计的扩展程序更让 Chrome 如虎添翼。在本文当中，翼帆远航将分类整理出一些实用的 Chrome 扩展程序，同时也欢迎你分享出自己喜爱的 Chrome 扩展程序。本文不定期更新。</p><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">广告屏蔽</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、广告终结者 &nbsp;<a title=\"Chrome 网上应用店：广告终结者\" href=\"https://chrome.google.com/webstore/detail/%E5%B9%BF%E5%91%8A%E7%BB%88%E7%BB%93%E8%80%85/fpdnjdlbdmifoocedhkighhlbchbiikl?hl=zh-CN\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">添加</a><br style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"/><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(128, 128, 128);\">默认使用 ChinaList + EasyList，广告屏蔽效果不错，支持屏蔽自定义的网页元素，但不支持屏蔽国内视频网站的片头广告。</span></p><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">浏览器辅助</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、超级拖拽 &nbsp;<a title=\"Chrome 网上应用店：超级拖拽\" href=\"https://chrome.google.com/webstore/detail/drag-and-go/jaikcnhlohebodlpkmjepipngegjbfpg\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">添加</a><br style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"/><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(128, 128, 128);\">拖拽链接在新标签页打开，拖拽文字在新标签页搜索，支持自定义搜索引擎。</span></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">2、二维码生成器 &nbsp;<a title=\"Chrome 网上应用店：二维码生成器\" href=\"https://chrome.google.com/webstore/detail/%E4%BA%8C%E7%BB%B4%E7%A0%81%E7%94%9F%E6%88%90%E5%99%A8/ajaomcmkalmeeahjfdklkcjbljhbokjl/related\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">添加</a><br style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"/><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(128, 128, 128);\">点击工具栏上的“二维码生成器”按钮，即会显示当前页的二维码。</span></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">3、Thin Scroll Bar &nbsp;<a title=\"Chrome 网上应用店：Thin Scroll Bar\" href=\"https://chrome.google.com/webstore/detail/thin-scroll-bar/ojmmnceaidnmminjjffpndcbdibelgam\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">添加</a><br style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"/><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(128, 128, 128);\">让滚动条更细，页面可视范围更大。</span></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">4、Simple Undo Close &nbsp;<a title=\"Chrome 网上应用店：Simple Undo Close\" href=\"https://chrome.google.com/webstore/detail/simpleundoclose/emhohdghchmjepmigjojkehidlielknj\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">添加</a><br style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"/><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(128, 128, 128);\">撤销关闭页面。</span></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">5、Extensity &nbsp;<a title=\"Chrome 网上应用店：Extensity\" href=\"https://chrome.google.com/webstore/detail/extensity/jjmflmamggggndanpgfnpelongoepncg?hl=zh-CN\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">添加</a><br style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"/><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(128, 128, 128);\">通过 Extensity 在工具栏上的按钮，可以快速禁用或启用其它 Chrome 扩展程序。</span></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">6、Dream Afar New Tab &nbsp;<a href=\"http://www.ipeld.net/archives/7795.html\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">查看</a><br style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"/><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(128, 128, 128);\">在打开新标签页的同时，欣赏世界各地的风景，换一种心情开始一天的生活和工作。</span></p><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">开发工具</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">1、FeHelper (Web前端助手) &nbsp;<a title=\"Chrome 网上应用店：Web 前端助手 (FeHelper)\" href=\"https://chrome.google.com/webstore/detail/web%E5%89%8D%E7%AB%AF%E5%8A%A9%E6%89%8Bfehelper/pkgccpejnmalmdinmhkkfafefagiiiad\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">添加</a>&nbsp;&nbsp;<a title=\"Web 前端工具 (FeHelper) 的官方网站\" href=\"http://www.baidufe.com/fehelper\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">官方网站</a><br style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none;\"/><span style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(128, 128, 128);\">JSON 格式化、Unix 时间戳转换、代码压缩与格式化、正则表达式测试等工具。</span></p><p><br/></p>', '100', '1441014083', '1441014360', '1', '3', '0');
INSERT INTO `cms_article` VALUES ('29', 'Listary & Everything - 大幅提升文件浏览及', '<p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">现在几乎所有软件都会用到「打开/保存文件的浏览对话框」，但实际上它和「Windows 资源管理器」一样低效。当一些文件藏得很深，我们不得不层层深入文件夹，然后不断的“向上”来切换到其它目录。如果你是个热衷于追求高效率的人，那么如此频繁而乏味的操作简直是噩梦。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">对于日常操作而言，如果我们能简化其流程，缩短时间开销，那么整体效率无疑就大大的提高了。翼帆远航今天要介绍的两款工具：Listary 和 Everything 就是这样能够简化文件浏览及搜索流程的神器，彻底解放你的双手，大幅提高效率。</p><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">Listary</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">Listary 是一款免费的资源管理器增强插件，支持简体中文，安装后在资源管理器或文件对话框的界面下，只要轻敲键盘，输入字母就能轻松定位和操作文件了，非常赞的是，他竟然支持用拼音缩写来匹配中文文件名。当你习惯了使用 Listary 之后，你一定不愿意再回到过去！</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">Listary 十分轻巧，运行快速，即使在低配的电脑上常驻系统使用也不会察觉到拖慢系统的现象。另外，软件本身提供了完善的中文教学视频，上手是非常容易的。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/01/listary-01.png\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone size-large wp-image-8538\" src=\"http://www.ipeld.net/wp-content/themes/sahifa/images/grey.gif\" alt=\"listary-01\" width=\"618\" height=\"364\" original=\"http://www.ipeld.net/wp-content/uploads/2015/01/listary-01-1024x603.png\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto;\"/></a></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/01/listary-02.png\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone size-large wp-image-8539\" src=\"http://www.ipeld.net/wp-content/themes/sahifa/images/grey.gif\" alt=\"listary-02\" width=\"618\" height=\"413\" original=\"http://www.ipeld.net/wp-content/uploads/2015/01/listary-02-1024x685.png\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto;\"/></a></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">另外，Listary 也支持 WinRAR 等第三方软件。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/01/listary-03.png\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone size-large wp-image-8540\" src=\"http://www.ipeld.net/wp-content/themes/sahifa/images/grey.gif\" alt=\"listary-03\" width=\"618\" height=\"364\" original=\"http://www.ipeld.net/wp-content/uploads/2015/01/listary-03-1024x603.png\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto;\"/></a></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">Listary 是免费的，可以满足日常工作的需要，如果你想使用增强功能，建议升级到 Listary Pro (付费)。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">官网链接：&nbsp;<a href=\"http://www.listary.com/download/Listary.exe\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">下载 Listary</a>&nbsp;&nbsp;| &nbsp;<a href=\"http://www.listary.com/\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">访问 Listary 官网</a></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">异次元软件世界：&nbsp;<a href=\"http://www.iplaysoft.com/listary.html\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">深入了解 Listary&nbsp;Pro</a>&nbsp; | &nbsp;<a href=\"http://item.taobao.com/item.htm?id=20722635882\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">购买 Listary Pro</a></p><h3 style=\"padding: 0px; margin: 60px 0px 30px; outline: none; list-style: none; border: 0px none; font-weight: normal; line-height: 1em; font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; font-size: 2em; color: rgb(129, 189, 0); white-space: normal; background-color: rgb(255, 255, 255);\">Everything</h3><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\">Everything 是迄今为止最优秀的文件搜索工具，它能闪电般的从硬盘海量的文件中找到你需要的文件。Everything 有着轻巧的体积和极低的内存占用率，完胜其它文件搜索工具。不过，Everything 只是基于文件名的搜索，如果你经常需要搜索文件内容，不妨试试 FileLocator Pro。</p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://www.ipeld.net/wp-content/uploads/2015/01/everything-01.png\" class=\"highslide-image\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: none !important; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important; cursor: url(http://www.ipeld.net/wp-content/plugins/auto-highslide/highslide/graphics/zoomin.cur), pointer;\"><img class=\"alignnone size-medium wp-image-8544\" src=\"http://www.ipeld.net/wp-content/themes/sahifa/images/grey.gif\" alt=\"everything-01\" width=\"450\" height=\"383\" original=\"http://www.ipeld.net/wp-content/uploads/2015/01/everything-01-450x383.png\" style=\"padding: 0px; margin: 0px; outline: none; list-style: none; border: 1px solid rgb(238, 238, 238); max-width: 100%; height: auto;\"/></a></p><p style=\"padding: 0.5em 0px; margin-top: 1em; margin-bottom: 1em; outline: none; list-style: none; border: 0px none; font-size: 1.2em; line-height: 1.75; color: rgb(51, 51, 51); font-family: &#39;Microsoft Yahei&#39;, 微软雅黑, Arial, Verdana, sans-serif; white-space: normal; background-color: rgb(255, 255, 255);\"><a href=\"http://pan.baidu.com/s/1pJkI2gV\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">下载 Everything</a>&nbsp;&nbsp;| &nbsp;<a href=\"http://www.iplaysoft.com/everything.html\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">深入了解 Everything</a>&nbsp;&nbsp; | &nbsp;<a href=\"http://xbeta.info/everything-search-tool.htm\" target=\"_blank\" style=\"padding: 0px !important; margin: 0px; outline: none; list-style: none; border: 0px none; color: rgb(129, 189, 0); text-decoration: none; transition: none !important; -webkit-transition: none !important;\">使用说明书</a></p><p><br/></p>', '100', '1441014135', '1441014160', '1', '0', '0');

-- ----------------------------
-- Table structure for cms_auth
-- ----------------------------
DROP TABLE IF EXISTS `cms_auth`;
CREATE TABLE `cms_auth` (
  `auth_id` smallint(6) unsigned NOT NULL AUTO_INCREMENT,
  `auth_name` varchar(20) NOT NULL COMMENT '名称',
  `auth_pid` smallint(6) unsigned NOT NULL COMMENT '父id',
  `auth_c` varchar(32) NOT NULL DEFAULT '' COMMENT '模块',
  `auth_a` varchar(32) NOT NULL DEFAULT '' COMMENT '操作方法',
  `auth_path` varchar(32) NOT NULL COMMENT '全路径',
  `auth_level` tinyint(4) NOT NULL DEFAULT '0' COMMENT '基别',
  PRIMARY KEY (`auth_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='后台角色对应的权限表';

-- ----------------------------
-- Records of cms_auth
-- ----------------------------

-- ----------------------------
-- Table structure for cms_brand
-- ----------------------------
DROP TABLE IF EXISTS `cms_brand`;
CREATE TABLE `cms_brand` (
  `brand_id` smallint(5) unsigned NOT NULL AUTO_INCREMENT,
  `brand_name` varchar(60) NOT NULL DEFAULT '' COMMENT '品牌名称',
  `brand_big_logo` varchar(80) NOT NULL DEFAULT '' COMMENT '品牌大标志',
  `brand_small_logo` varchar(80) NOT NULL DEFAULT '' COMMENT '品牌小标志',
  `brand_desc` text NOT NULL COMMENT '品牌描述',
  `brand_url` varchar(255) NOT NULL DEFAULT '' COMMENT '品牌网址',
  PRIMARY KEY (`brand_id`)
) ENGINE=InnoDB DEFAULT CHARSET=utf8 COMMENT='商品品牌表';

