DROP TABLE IF EXISTS `cms_comment`;
CREATE TABLE `cms_comment` (
  `cm_id` int(10) NOT NULL AUTO_INCREMENT COMMENT '评论ID',
  `cm_nickname` varchar(50) NOT NULL COMMENT '评论者名称',
  `cm_url` varchar(50) NOT NULL COMMENT '评论者链接地址',
  `cm_content` text NOT NULL COMMENT '评论者内容',
  `cm_ip` varchar(12) NOT NULL COMMENT '评论者IP',
  `cm_email` varchar(255) NOT NULL,
