-- ----------------------------
-- Table structure for cms_user
-- ----------------------------
DROP TABLE IF EXISTS `cms_user`;
CREATE TABLE `cms_user` (
  `user_id` int(11) NOT NULL AUTO_INCREMENT COMMENT '自增id',
  `user_email` varchar(64) NOT NULL DEFAULT '' COMMENT '邮箱,作为登录名',
  `username` varchar(128) NOT NULL DEFAULT '' COMMENT '用户名，可以重复更改',
  `password` varchar(35) NOT NULL DEFAULT '' COMMENT '登录密码',
  `user_code` varchar(32) DEFAULT NULL COMMENT '用来邮箱验证防伪用',
  `user_identify` enum('0','1') NOT NULL DEFAULT '0' COMMENT '是否激活标志，默认0未激活',
  `user_sex` tinyint(4) NOT NULL DEFAULT '1' COMMENT '性别',
  `user_qq` varchar(32) NOT NULL DEFAULT '' COMMENT 'qq',
  `user_tel` varchar(32) NOT NULL DEFAULT '' COMMENT '手机',
  `user_xueli` tinyint(4) NOT NULL DEFAULT '1' COMMENT '学历',
  `user_hobby` varchar(32) NOT NULL DEFAULT '' COMMENT '爱好',
  `user_introduce` text COMMENT '简介',
  `user_time` int(11) DEFAULT NULL COMMENT '用户注册时间',
  `user_last` int(11) DEFAULT '0' COMMENT '用户上次登陆时间',
  PRIMARY KEY (`user_id`)
) ENGINE=InnoDB AUTO_INCREMENT=25 DEFAULT CHARSET=utf8 COMMENT='会员表';

